@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Playfair+Display:wght@400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Clean Dark Theme Colors */
  --background: hsl(222, 84%, 5%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 8%);
  --card-foreground: hsl(210, 40%, 95%);
  --popover: hsl(222, 84%, 8%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(210, 40%, 98%);
  --primary-foreground: hsl(222, 84%, 5%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(215, 20%, 65%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(263, 70%, 50%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(263, 70%, 50%);
  --success: hsl(142, 76%, 36%);

  /* Mystical Accent Colors */
  --mystical-purple: hsl(263, 70%, 50%);
  --mystical-violet: hsl(280, 60%, 55%);
  --mystical-gold: hsl(45, 90%, 60%);

  --font-sans: 'Inter', sans-serif;
  --font-serif: 'Playfair Display', serif;
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    line-height: 1.6;
  }
}

/* Typography */
.font-heading {
  font-family: var(--font-serif);
}

/* Simple gradient background */
.gradient-bg {
  background: linear-gradient(135deg, hsl(222, 84%, 5%) 0%, hsl(222, 84%, 8%) 50%, hsl(217, 32%, 17%) 100%);
}

/* Subtle animated background */
.subtle-bg {
  background: 
    radial-gradient(circle at 20% 80%, hsl(263, 70%, 50%, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, hsl(280, 60%, 55%, 0.1) 0%, transparent 50%),
    linear-gradient(135deg, hsl(222, 84%, 5%) 0%, hsl(222, 84%, 8%) 100%);
}

/* Clean card styling */
.clean-card {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
}

.clean-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 15px -3px rgba(0, 0, 0, 0.2);
  border-color: var(--accent);
}

/* Accent gradient */
.accent-gradient {
  background: linear-gradient(135deg, var(--mystical-purple), var(--mystical-violet));
}

/* Subtle glow effect */
.subtle-glow {
  box-shadow: 0 0 20px rgba(133, 77, 250, 0.2);
}

/* Text gradient */
.text-gradient {
  background: linear-gradient(135deg, var(--mystical-purple), var(--mystical-violet));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Button hover effects */
.hover-lift {
  transition: transform 0.2s ease;
}

.hover-lift:hover {
  transform: translateY(-1px);
}

/* Cosmic loader animations */
@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes spin-reverse {
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes float-delayed {
  0%, 100% { transform: translateY(-5px); }
  50% { transform: translateY(5px); }
}

@keyframes fade-in {
  0% { opacity: 0; transform: translateY(10px); }
  100% { opacity: 1; transform: translateY(0); }
}

@keyframes shooting-star {
  0% {
    transform: translateX(-100px) translateY(100px);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateX(100vw) translateY(-100px);
    opacity: 0;
  }
}

.animate-spin-slow {
  animation: spin-slow 4s linear infinite;
}

.animate-spin-reverse {
  animation: spin-reverse 3s linear infinite;
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-float-delayed {
  animation: float-delayed 3s ease-in-out infinite;
}

.animate-fade-in {
  animation: fade-in 0.5s ease-out;
}

.shooting-star {
  position: absolute;
  width: 2px;
  height: 2px;
  background: linear-gradient(45deg, white, transparent);
  border-radius: 50%;
  box-shadow: 0 0 10px white;
  animation: shooting-star 6s linear infinite;
}

.shooting-star::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 50px;
  height: 1px;
  background: linear-gradient(90deg, white, transparent);
  transform-origin: 0 0;
  transform: rotate(45deg);
}

