import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Sparkles, Zap, Shuffle, Loader2 } from "lucide-react";
import { TarotAnalysisResult, TarotInput } from "@shared/schema";
import { CosmicLoader } from "@/components/cosmic-loader";

interface TarotAnalysisInterfaceProps {
  onAnalysisComplete: (result: TarotAnalysisResult, inputData: TarotInput, analysisId: string) => void;
}

// Predefined tarot cards for realistic simulation
const MAJOR_ARCANA = [
  "The Fool", "The Magician", "The High Priestess", "The Empress", "The Emperor",
  "The Hierophant", "The Lovers", "The Chariot", "Strength", "The Hermit",
  "Wheel of Fortune", "Justice", "The Hanged Man", "Death", "Temperance",
  "The Devil", "The Tower", "The Star", "The Moon", "The Sun", "Judgement", "The World"
];

const MINOR_ARCANA = [
  // Wands
  "Ace of Wands", "Two of Wands", "Three of Wands", "Four of Wands", "Five of Wands",
  "Six of Wands", "Seven of Wands", "Eight of Wands", "Nine of Wands", "Ten of Wands",
  "Page of Wands", "Knight of Wands", "Queen of Wands", "King of Wands",
  // Cups
  "Ace of Cups", "Two of Cups", "Three of Cups", "Four of Cups", "Five of Cups",
  "Six of Cups", "Seven of Cups", "Eight of Cups", "Nine of Cups", "Ten of Cups",
  "Page of Cups", "Knight of Cups", "Queen of Cups", "King of Cups",
  // Swords
  "Ace of Swords", "Two of Swords", "Three of Swords", "Four of Swords", "Five of Swords",
  "Six of Swords", "Seven of Swords", "Eight of Swords", "Nine of Swords", "Ten of Swords",
  "Page of Swords", "Knight of Swords", "Queen of Swords", "King of Swords",
  // Pentacles
  "Ace of Pentacles", "Two of Pentacles", "Three of Pentacles", "Four of Pentacles", "Five of Pentacles",
  "Six of Pentacles", "Seven of Pentacles", "Eight of Pentacles", "Nine of Pentacles", "Ten of Pentacles",
  "Page of Pentacles", "Knight of Pentacles", "Queen of Pentacles", "King of Pentacles"
];

const ALL_CARDS = [...MAJOR_ARCANA, ...MINOR_ARCANA];

export function TarotAnalysisInterface({ onAnalysisComplete }: TarotAnalysisInterfaceProps) {
  const [spreadType, setSpreadType] = useState<"single-card" | "three-card" | "celtic-cross">("three-card");
  const [question, setQuestion] = useState("");
  const [drawnCards, setDrawnCards] = useState<Array<{cardName: string; suit: string; position: string; reversed: boolean}>>([]);
  const [isDrawingCards, setIsDrawingCards] = useState(false);

  const { toast } = useToast();

  const analysisMutation = useMutation({
    mutationFn: async (tarotData: TarotInput) => {
      const response = await apiRequest("POST", "/api/tarot/analyze", tarotData);
      return response.json();
    },
    onSuccess: (data) => {
      onAnalysisComplete(data.result, data.inputData, data.id);
      toast({
        title: "Reading Complete!",
        description: "Your tarot reading is ready.",
      });
    },
    onError: (error) => {
      toast({
        title: "Reading Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getSpreadPositions = (type: string) => {
    switch (type) {
      case "single-card":
        return ["Present Situation"];
      case "three-card":
        return ["Past", "Present", "Future"];
      case "celtic-cross":
        return [
          "Present Situation", "Challenge", "Distant Past", "Recent Past",
          "Possible Outcome", "Near Future", "Your Approach", "External Influences",
          "Hopes and Fears", "Final Outcome"
        ];
      default:
        return ["Past", "Present", "Future"];
    }
  };

  const drawCards = () => {
    setIsDrawingCards(true);
    const positions = getSpreadPositions(spreadType);
    const selectedCards: Array<{cardName: string; suit: string; position: string; reversed: boolean}> = [];
    const availableCards = [...ALL_CARDS];

    // Simulate card drawing with animation delay
    setTimeout(() => {
      for (let i = 0; i < positions.length; i++) {
        const randomIndex = Math.floor(Math.random() * availableCards.length);
        const cardName = availableCards.splice(randomIndex, 1)[0];
        const reversed = Math.random() < 0.3; // 30% chance of reversed card

        // Determine suit for minor arcana
        let suit = "";
        if (cardName.includes("Wands")) suit = "Wands";
        else if (cardName.includes("Cups")) suit = "Cups";
        else if (cardName.includes("Swords")) suit = "Swords";
        else if (cardName.includes("Pentacles")) suit = "Pentacles";
        else suit = "Major Arcana";

        selectedCards.push({
          cardName,
          suit,
          position: positions[i],
          reversed
        });
      }

      setDrawnCards(selectedCards);
      setIsDrawingCards(false);
    }, 1500);
  };

  const handleAnalyze = () => {
    if (drawnCards.length === 0) {
      toast({
        title: "No Cards Drawn",
        description: "Please draw cards first before analyzing.",
        variant: "destructive",
      });
      return;
    }

    const tarotData: TarotInput = {
      spreadType,
      question: question.trim() || undefined,
      drawnCards,
    };

    analysisMutation.mutate(tarotData);
  };

  const resetReading = () => {
    setQuestion("");
    setDrawnCards([]);
    setSpreadType("three-card");
  };

  return (
    <div className="w-full max-w-2xl mx-auto">
      <Card className="bg-gradient-to-br from-indigo-50 to-purple-50 border-indigo-200">
        <CardHeader className="text-center">
          <div className="mx-auto w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center mb-4">
            <Zap className="h-6 w-6 text-indigo-600" />
          </div>
          <CardTitle className="text-2xl text-indigo-800">Tarot Reading</CardTitle>
          <CardDescription className="text-indigo-600">
            Discover guidance and insights through the ancient wisdom of tarot
          </CardDescription>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Spread Type Selection */}
          <div className="space-y-2">
            <Label htmlFor="spreadType">Choose Your Spread</Label>
            <Select value={spreadType} onValueChange={(value: any) => setSpreadType(value)}>
              <SelectTrigger data-testid="select-tarot-spread">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="single-card">Single Card - Quick Insight</SelectItem>
                <SelectItem value="three-card">Three Card - Past, Present, Future</SelectItem>
                <SelectItem value="celtic-cross">Celtic Cross - Comprehensive Reading</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Question Input */}
          <div className="space-y-2">
            <Label htmlFor="question">Your Question (Optional)</Label>
            <Textarea
              id="question"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask the cards for guidance on love, career, spirituality, or any area of your life..."
              rows={3}
              data-testid="textarea-tarot-question"
            />
            <p className="text-sm text-muted-foreground">
              Focus your intention while asking your question for more meaningful insights
            </p>
          </div>

          {/* Card Drawing Section */}
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <h4 className="font-semibold text-indigo-800">Draw Your Cards</h4>
              <Button 
                onClick={drawCards}
                disabled={isDrawingCards}
                variant="outline"
                size="sm"
                data-testid="button-draw-tarot-cards"
              >
                {isDrawingCards ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Drawing...
                  </>
                ) : (
                  <>
                    <Shuffle className="mr-2 h-4 w-4" />
                    Draw Cards
                  </>
                )}
              </Button>
            </div>

            {/* Display Drawn Cards */}
            {drawnCards.length > 0 && (
              <div className="grid gap-3">
                {drawnCards.map((card, index) => (
                  <Card key={index} className="bg-white/70 border-indigo-100">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-medium text-indigo-800">{card.position}</p>
                          <p className="text-sm text-indigo-600">
                            {card.cardName} {card.reversed && "(Reversed)"}
                          </p>
                          {card.suit && card.suit !== "Major Arcana" && (
                            <p className="text-xs text-muted-foreground">{card.suit}</p>
                          )}
                        </div>
                        <div className="text-right">
                          <Sparkles className={`h-5 w-5 ${card.reversed ? 'transform rotate-180 text-orange-500' : 'text-yellow-500'}`} />
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>

          {/* Spread Information */}
          <Card className="bg-white/50 border-indigo-100">
            <CardContent className="p-4">
              <h4 className="font-semibold text-indigo-800 mb-2 flex items-center">
                <Zap className="mr-2 h-4 w-4" />
                {spreadType === "single-card" && "Single Card Reading"}
                {spreadType === "three-card" && "Three Card Reading"}
                {spreadType === "celtic-cross" && "Celtic Cross Reading"}
              </h4>
              <p className="text-sm text-indigo-700">
                {spreadType === "single-card" && "Perfect for quick daily guidance or focused questions about your current situation."}
                {spreadType === "three-card" && "Explores the past influences, present situation, and future potential around your question."}
                {spreadType === "celtic-cross" && "The most comprehensive spread, revealing complex layers of your situation with detailed guidance."}
              </p>
            </CardContent>
          </Card>

          {/* Action Buttons */}
          <div className="space-y-3">
            <Button 
              onClick={handleAnalyze}
              disabled={analysisMutation.isPending || drawnCards.length === 0}
              className="w-full bg-indigo-600 hover:bg-indigo-700"
              size="lg"
              data-testid="button-analyze-tarot"
            >
              {analysisMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {analysisMutation.isPending ? "Interpreting Cards..." : "Interpret Reading"}
            </Button>

            <Button 
              onClick={resetReading}
              variant="outline"
              className="w-full"
              data-testid="button-reset-tarot-reading"
            >
              New Reading
            </Button>
          </div>
        </CardContent>
      </Card>

      <CosmicLoader 
        analysisType="tarot"
        isVisible={analysisMutation.isPending}
      />
    </div>
  );
}

client/src/components/tarot-results.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { TarotAnalysisResult, TarotInput } from "@shared/schema";
import { PostAnalysisChat } from "./post-analysis-chat";
import { 
  Zap, 
  Sparkles, 
  Eye,
  Heart,
  Target,
  Compass,
  Download,
  Share,
  RotateCcw,
  Clock,
  Star,
  ArrowRight
} from "lucide-react";

interface TarotResultsProps {
  result: TarotAnalysisResult;
  inputData: TarotInput;
  onAnalyzeAnother: () => void;
  analysisId?: string;
  isAuthenticated?: boolean;
  onLoginRequired?: () => void;
}

export function TarotResults({ 
  result, 
  inputData, 
  onAnalyzeAnother, 
  analysisId, 
  isAuthenticated = false, 
  onLoginRequired 
}: TarotResultsProps) {
  const handleDownloadReport = () => {
    const reportContent = `TAROT READING REPORT\\n\\n` +
      `Spread Type: ${result.spreadType}\\n` +
      (inputData.question ? `Question: ${inputData.question}\\n` : '') +
      `\\nPersonality Overview:\\n${result.personalityOverview}\\n\\n` +
      `Card Analysis:\\n` +
      result.cardAnalysis.map((card, index) => 
        `${index + 1}. ${card.position}: ${card.cardName}${card.reversed ? ' (Reversed)' : ''}\\n` +
        `   Meaning: ${card.meaning}\\n` +
        `   Interpretation: ${card.interpretation}\\n`
      ).join('\\n') +
      `\\nOverall Message:\\n${result.overallMessage}\\n\\n` +
      `Guidance:\\n` +
      `Present Situation: ${result.guidance.presentSituation}\\n` +
      (result.guidance.pastInfluences ? `Past Influences: ${result.guidance.pastInfluences}\\n` : '') +
      (result.guidance.futureOutlook ? `Future Outlook: ${result.guidance.futureOutlook}\\n` : '') +
      `Advice: ${result.guidance.advice}\\n` +
      (result.guidance.outcome ? `Outcome: ${result.guidance.outcome}\\n` : '') +
      `\\nAction Steps:\\n${result.actionSteps.map((step, i) => `${i + 1}. ${step}`).join('\\n')}\\n\\n` +
      `Reflection:\\n${result.reflection}\\n\\n` +
      `Generated by MysticRead AI - ${new Date().toLocaleDateString()}`;

    const blob = new Blob([reportContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `tarot-reading-${new Date().getTime()}.txt`;
    link.click();
    URL.revokeObjectURL(url);
  };

  const handleShareResults = () => {
    const shareText = `I just received a ${result.spreadType} tarot reading from MysticRead AI! ` +
      `Cards drawn: ${result.cardAnalysis.map(c => c.cardName).join(', ')}. ` +
      `The insights were truly enlightening! Check out MysticRead AI for your own tarot reading.`;

    if (navigator.share) {
      navigator.share({
        title: 'My Tarot Reading',
        text: shareText,
      });
    } else {
      navigator.clipboard.writeText(shareText).then(() => {
        alert('Reading copied to clipboard!');
      });
    }
  };

  const getSpreadDisplayName = (spreadType: string) => {
    switch (spreadType) {
      case "single-card": return "Single Card Reading";
      case "three-card": return "Three Card Reading";
      case "celtic-cross": return "Celtic Cross Reading";
      default: return "Tarot Reading";
    }
  };

  return (
    <section id="tarot-results-section" className="py-20 bg-white">
      <div className="container mx-auto px-4">
        <div className="max-w-6xl mx-auto">

          {/* Results Header */}
          <div className="text-center mb-12">
            <h3 className="text-3xl font-bold text-primary mb-4">Your Tarot Reading</h3>
            <p className="text-lg text-secondary">
              {getSpreadDisplayName(result.spreadType)}
              {inputData.question && (
                <span className="block text-indigo-600 mt-2 italic">
                  "{inputData.question}"
                </span>
              )}
            </p>
          </div>

          {/* Card Analysis */}
          <div className="grid lg:grid-cols-2 xl:grid-cols-3 gap-6 mb-12">
            {result.cardAnalysis.map((card, index) => (
              <Card key={index} className="bg-gradient-to-br from-indigo-50 to-purple-50 border-indigo-200">
                <CardContent className="p-6">
                  <div className="text-center mb-4">
                    <div className="w-16 h-16 bg-indigo-100 rounded-full flex items-center justify-center mx-auto mb-3">
                      <Sparkles className={`h-8 w-8 ${card.reversed ? 'transform rotate-180 text-orange-600' : 'text-indigo-600'}`} />
                    </div>
                    <h4 className="font-semibold text-indigo-800 mb-1">{card.position}</h4>
                    <p className="text-sm font-medium text-indigo-600">
                      {card.cardName}
                      {card.reversed && (
                        <Badge variant="outline" className="ml-2 text-orange-600 border-orange-200">
                          Reversed
                        </Badge>
                      )}
                    </p>
                  </div>
                  <div className="space-y-3">
                    <div>
                      <h5 className="font-medium text-purple-800 mb-1">Meaning</h5>
                      <p className="text-sm text-secondary">{card.meaning}</p>
                    </div>
                    <div>
                      <h5 className="font-medium text-blue-800 mb-1">Interpretation</h5>
                      <p className="text-sm text-secondary">{card.interpretation}</p>
                    </div>
                    {card.reversed && card.reversedMeaning && (
                      <div>
                        <h5 className="font-medium text-orange-800 mb-1">Reversed Meaning</h5>
                        <p className="text-sm text-secondary">{card.reversedMeaning}</p>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Overall Analysis */}
          <div className="grid lg:grid-cols-2 gap-8 mb-12">

            {/* Personality Overview */}
            <Card>
              <CardContent className="p-6">
                <h4 className="font-semibold text-primary mb-4 flex items-center">
                  <Eye className="mr-2 h-5 w-5 text-purple-600" />
                  Personality Insights
                </h4>
                <p className="text-secondary leading-relaxed">
                  {result.personalityOverview}
                </p>
              </CardContent>
            </Card>

            {/* Overall Message */}
            <Card>
              <CardContent className="p-6">
                <h4 className="font-semibold text-primary mb-4 flex items-center">
                  <Zap className="mr-2 h-5 w-5 text-indigo-600" />
                  Overall Message
                </h4>
