  );
}

client/src/components/ai-chat-interface..content:
import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { 
  MessageCircle, 
  Send, 
  Loader2, 
  Sparkles, 
  Clock, 
  CreditCard, 
  AlertCircle,
  Bot,
  User
} from "lucide-react";
import { RazorpayPayment } from "./razorpay-payment";

interface ChatMessage {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

interface User {
  aiChatCredits: number;
  aiChatMinutesUsed: number;
}

export function AiChatInterface() {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputMessage, setInputMessage] = useState("");
  const [showPayment, setShowPayment] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get user data including AI chat credits
  const { data: user, isLoading: userLoading } = useQuery<User>({
    queryKey: ["/api/auth/me"],
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/ai-chat", { message });
      return response.json();
    },
    onSuccess: (data) => {
      // Add AI response to messages
      const aiMessage: ChatMessage = {
        id: Date.now().toString(),
        role: 'assistant',
        content: data.response,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, aiMessage]);

      // Update credits in user query
      queryClient.setQueryData(["/api/auth/me"], (oldData: any) => ({
        ...oldData,
        aiChatCredits: data.creditsRemaining
      }));

      toast({
        title: "Response Generated",
        description: `${data.creditsRemaining} credits remaining`,
      });
    },
    onError: (error: any) => {
      if (error.needsPayment) {
        setShowPayment(true);
        toast({
          title: "Credits Needed",
          description: "Purchase AI chat credits to continue the conversation",
          variant: "destructive"
        });
      } else {
        toast({
          title: "Chat Error",
          description: error.message || "Failed to send message",
          variant: "destructive"
        });
      }
    }
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim() || sendMessageMutation.isPending) return;

    // Add user message to messages
    const userMessage: ChatMessage = {
      id: Date.now().toString(),
      role: 'user', 
      content: inputMessage.trim(),
      timestamp: new Date()
    };
    setMessages(prev => [...prev, userMessage]);

    // Send to API
    sendMessageMutation.mutate(inputMessage.trim());
    setInputMessage("");
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handlePaymentSuccess = () => {
    setShowPayment(false);
    queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
    toast({
      title: "Credits Added!",
      description: "You can now continue your AI chat session",
    });
  };

  if (userLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <Loader2 className="h-8 w-8 animate-spin text-accent" />
      </div>
    );
  }

  // Show payment interface if no credits or user explicitly requested it
  if (!user?.aiChatCredits || showPayment) {
    return (
      <div className="space-y-6">
        <Card className="border-accent/20">
          <CardHeader className="text-center">
            <div className="mx-auto w-16 h-16 bg-accent/10 rounded-full flex items-center justify-center mb-4">
              <MessageCircle className="h-8 w-8 text-accent" />
            </div>
            <CardTitle className="text-2xl text-foreground">AI Mystical Assistant</CardTitle>
            <CardDescription>
              {user?.aiChatCredits ? (
                "Get more credits to continue your mystical journey"
              ) : (
                "Get personalized mystical guidance powered by AI. Purchase credits to start chatting."
              )}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-center space-y-4">
              {user?.aiChatCredits ? (
                <div className="flex items-center justify-center gap-4">
                  <Badge variant="secondary" className="text-sm">
                    <CreditCard className="h-4 w-4 mr-1" />
                    {user.aiChatCredits} credits remaining
                  </Badge>
                  <Button
                    onClick={() => setShowPayment(false)}
                    variant="outline"
                    size="sm"
                  >
                    Back to Chat
                  </Button>
                </div>
              ) : (
                <div className="flex items-center justify-center gap-2 text-orange-600 dark:text-orange-400">
                  <AlertCircle className="h-5 w-5" />
                  <span className="text-sm font-medium">No AI chat credits available</span>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <RazorpayPayment onPaymentSuccess={handlePaymentSuccess} />
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header with Credit Info */}
      <Card className="border-accent/20">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-accent/10 rounded-full flex items-center justify-center">
                <Sparkles className="h-5 w-5 text-accent" />
              </div>
              <div>
                <CardTitle className="text-lg">AI Mystical Assistant</CardTitle>
                <CardDescription>Your guide to mystical insights</CardDescription>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <Badge variant="secondary" className="text-sm">
                <CreditCard className="h-4 w-4 mr-1" />
                {user.aiChatCredits} credits
              </Badge>
              <Badge variant="outline" className="text-sm">
                <Clock className="h-4 w-4 mr-1" />
                {user.aiChatMinutesUsed.toFixed(1)}m used
              </Badge>
              <Button
                onClick={() => setShowPayment(true)}
                variant="outline"
                size="sm"
                data-testid="button-buy-credits"
              >
                Buy More Credits
              </Button>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Chat Interface */}
      <Card className="h-[600px] flex flex-col">
        <CardContent className="flex-1 flex flex-col p-0">
          {/* Messages Area */}
          <ScrollArea className="flex-1 p-6">
            <div className="space-y-4">
              {messages.length === 0 ? (
                <div className="text-center text-muted-foreground py-12">
                  <Bot className="h-12 w-12 mx-auto mb-4 text-accent/50" />
                  <h3 className="text-lg font-medium mb-2">Welcome to AI Mystical Chat</h3>
                  <p className="text-sm max-w-md mx-auto">
                    Ask me anything about spirituality, mysticism, or seek guidance on your life's journey. 
                    Each message uses 1 credit.
                  </p>
                </div>
              ) : (
                messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`flex gap-3 max-w-[80%] ${message.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                        message.role === 'user' 
                          ? 'bg-accent text-accent-foreground' 
                          : 'bg-muted text-muted-foreground'
                      }`}>
                        {message.role === 'user' ? (
                          <User className="h-4 w-4" />
                        ) : (
                          <Bot className="h-4 w-4" />
                        )}
                      </div>
                      <div className={`rounded-lg px-4 py-3 ${
                        message.role === 'user'
                          ? 'bg-accent text-accent-foreground'
                          : 'bg-muted text-foreground'
                      }`}>
                        <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                        <p className="text-xs opacity-70 mt-1">
                          {message.timestamp.toLocaleTimeString()}
                        </p>
                      </div>
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>
          </ScrollArea>

          {/* Input Area */}
          <div className="border-t p-4">
            <div className="flex gap-2">
              <Input
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me anything mystical..."
                disabled={sendMessageMutation.isPending}
                className="flex-1"
                data-testid="input-ai-chat-message"
              />
              <Button
                onClick={handleSendMessage}
                disabled={!inputMessage.trim() || sendMessageMutation.isPending}
                size="icon"
                data-testid="button-send-ai-chat"
              >
                {sendMessageMutation.isPending ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <Send className="h-4 w-4" />
                )}
              </Button>
            </div>
            <p className="text-xs text-muted-foreground mt-2">
              Each message uses 1 credit â€¢ {user.aiChatCredits} credits remaining
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/cosmic-loader.tsx.content:
import { useEffect, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Sparkles, Stars, Zap } from "lucide-react";

interface CosmicLoaderProps {
  analysisType?: 'palm' | 'astrology' | 'vastu' | 'numerology' | 'tarot';
  message?: string;
  isVisible?: boolean;
}

const analysisMessages = {
  palm: [
    "Reading the lines of destiny...",
    "Interpreting the ancient palm wisdom...",
    "Unveiling your life's journey...",
    "Discovering your inner strengths..."
  ],
  astrology: [
    "Aligning with celestial energies...",
    "Mapping your cosmic blueprint...",
    "Reading the stars' ancient messages...",
    "Channeling planetary influences..."
  ],
  vastu: [
    "Analyzing energy flow patterns...",
    "Harmonizing spatial vibrations...",
    "Balancing elemental forces...",
    "Optimizing cosmic alignment..."
  ],
  numerology: [
    "Calculating mystical number patterns...",
    "Decoding numerical vibrations...",
    "Unlocking hidden frequencies...",
    "Revealing sacred geometries..."
  ],
  tarot: [
    "Shuffling the cosmic deck...",
    "Drawing from universal wisdom...",
    "Connecting with ancient archetypes...",
    "Channeling mystical insights..."
  ]
};

export function CosmicLoader({ analysisType = 'palm', message, isVisible = true }: CosmicLoaderProps) {
  const [currentMessage, setCurrentMessage] = useState(0);
  const [particles, setParticles] = useState<Array<{id: number, x: number, y: number, delay: number, duration: number}>>([]);

  const messages = message ? [message] : analysisMessages[analysisType];

  useEffect(() => {
    // Generate random particles
    const newParticles = Array.from({ length: 20 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      delay: Math.random() * 2,
      duration: 2 + Math.random() * 3
    }));
    setParticles(newParticles);

    // Cycle through messages
    if (messages.length > 1) {
      const interval = setInterval(() => {
        setCurrentMessage(prev => (prev + 1) % messages.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [analysisType, messages.length]);

  if (!isVisible) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm z-50 flex items-center justify-center">
      <Card className="relative overflow-hidden bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 border-purple-500 shadow-2xl">
        <CardContent className="p-12 text-center relative">
          {/* Animated background stars */}
          <div className="absolute inset-0 overflow-hidden">
            {particles.map(particle => (
              <div
                key={particle.id}
                className="absolute w-1 h-1 bg-white rounded-full animate-pulse"
                style={{
                  left: `${particle.x}%`,
                  top: `${particle.y}%`,
                  animationDelay: `${particle.delay}s`,
                  animationDuration: `${particle.duration}s`
                }}
              />
            ))}

            {/* Larger floating stars */}
            <div className="absolute top-6 left-8 animate-float">
              <Stars className="h-4 w-4 text-yellow-300 opacity-70" />
            </div>
            <div className="absolute top-12 right-12 animate-float-delayed">
              <Sparkles className="h-3 w-3 text-blue-300 opacity-60" />
            </div>
            <div className="absolute bottom-8 left-16 animate-float">
              <Zap className="h-3 w-3 text-purple-300 opacity-50" />
            </div>
            <div className="absolute bottom-16 right-8 animate-float-delayed">
              <Stars className="h-5 w-5 text-indigo-300 opacity-80" />
            </div>
          </div>

          {/* Main content */}
          <div className="relative z-10">
            {/* Central cosmic animation */}
            <div className="relative mb-8">
              <div className="w-24 h-24 mx-auto relative">
                {/* Outer ring */}
                <div className="absolute inset-0 border-4 border-purple-400 rounded-full animate-spin-slow opacity-60"></div>

                {/* Middle ring */}
                <div className="absolute inset-2 border-2 border-blue-400 rounded-full animate-spin-reverse opacity-80"></div>

                {/* Inner glow */}
                <div className="absolute inset-4 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full animate-pulse shadow-lg shadow-purple-500/50"></div>

                {/* Central star */}
                <div className="absolute inset-0 flex items-center justify-center">
                  <Sparkles className="h-8 w-8 text-white animate-pulse" />
                </div>
              </div>

              {/* Orbital particles */}
              <div className="absolute inset-0 animate-spin-slow">
                <div className="absolute top-0 left-1/2 w-2 h-2 bg-yellow-400 rounded-full transform -translate-x-1/2 animate-pulse"></div>
              </div>
              <div className="absolute inset-0 animate-spin-reverse">
                <div className="absolute bottom-0 right-1/2 w-1 h-1 bg-blue-400 rounded-full transform translate-x-1/2 animate-pulse"></div>
              </div>
            </div>

            {/* Loading message */}
            <div className="space-y-4">
              <h3 className="text-2xl font-semibold text-white mb-2">
                Cosmic Analysis in Progress
              </h3>

              <div className="h-6 flex items-center justify-center">
                <p 
                  key={currentMessage}
                  className="text-purple-200 animate-fade-in text-lg"
                  data-testid="text-loading-message"
                >
                  {messages[currentMessage]}
                </p>
              </div>

              {/* Loading dots */}
              <div className="flex justify-center space-x-2 mt-6">
                <div className="w-3 h-3 bg-purple-400 rounded-full animate-bounce"></div>
                <div className="w-3 h-3 bg-blue-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                <div className="w-3 h-3 bg-indigo-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
              </div>
            </div>
          </div>

          {/* Shooting star effect */}
          <div className="absolute top-0 left-0 w-full h-full overflow-hidden pointer-events-none">
            <div className="shooting-star"></div>
            <div className="shooting-star" style={{animationDelay: '3s'}}></div>
          </div>
        </CardContent>
      </Card>

    </div>
  );
}

client/src/components/razorpay-payment.t.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Check, Clock, MessageCircle, Zap, Loader2, CheckCircle, CreditCard } from "lucide-react";

interface PaymentTier {
  id: string;
  name: string;
  credits: number;
  minutes: number;
  price: number;
  popular?: boolean;
  description: string;
  features: string[];
}

const paymentTiers: PaymentTier[] = [
  {
    id: "tier1",
    name: "Starter",
    credits: 5,
    minutes: 5,
    price: 1,
    description: "Perfect for quick questions",
    features: [
      "5 minutes of AI chat",
      "5 chat credits",
      "Mystical guidance",
      "24/7 availability"
    ]
  },
  {
    id: "tier2",
    name: "Explorer",
    credits: 10,
    minutes: 10,
    price: 2.5,
    popular: true,
    description: "Great for deeper insights",
    features: [
      "10 minutes of AI chat",
      "10 chat credits",
      "Extended conversations",
      "Detailed guidance",
      "Priority support"
    ]
  },
  {
    id: "tier3",
    name: "Mystic",
    credits: 15,
    minutes: 15,
    price: 5,
    description: "Comprehensive spiritual guidance",
    features: [
      "15 minutes of AI chat",
      "15 chat credits",
      "Unlimited questions",
      "Deep spiritual insights",
      "Premium features",
      "Expert guidance"
    ]
  }
];

interface RazorpayPaymentProps {
  onPaymentSuccess: () => void;
}

export function RazorpayPayment({ onPaymentSuccess }: RazorpayPaymentProps) {
  const [selectedTier, setSelectedTier] = useState<PaymentTier | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [paymentStep, setPaymentStep] = useState<'processing' | 'success' | 'error'>('processing');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createOrderMutation = useMutation({
    mutationFn: async (tier: PaymentTier) => {
      const response = await apiRequest("POST", "/api/payments/create-order", {
        amount: tier.price,
        creditsRequested: tier.credits,
        paymentTier: tier.id,
        minutesGranted: tier.minutes
      });
      return response.json();
    }
  });

  const verifyPaymentMutation = useMutation({
    mutationFn: async (paymentData: { paymentId: string; tier: PaymentTier }) => {
      // Simulate payment verification with mock data
      const response = await apiRequest("POST", "/api/payments/verify", {
        razorpay_payment_id: `pay_mock_${Date.now()}`,
        razorpay_order_id: `order_mock_${Date.now()}`,
        razorpay_signature: `mock_signature_${Date.now()}`,
        paymentId: paymentData.paymentId
      });
      return { response: response.json(), tier: paymentData.tier };
    }
  });

  const handlePurchase = async (tier: PaymentTier) => {
    setIsProcessing(true);
    setSelectedTier(tier);
    setShowPaymentModal(true);
    setPaymentStep('processing');

    try {
      // Step 1: Create order
      const orderData = await createOrderMutation.mutateAsync(tier);

      // Step 2: Simulate payment processing delay (2-3 seconds)
      await new Promise(resolve => setTimeout(resolve, 2500));

      // Step 3: Process payment verification
      await verifyPaymentMutation.mutateAsync({ 
        paymentId: orderData.paymentId, 
        tier 
      });

      // Step 4: Show success
      setPaymentStep('success');

      // Step 5: Update UI after a short delay
      setTimeout(() => {
        toast({
          title: "Payment Successful! ðŸŽ‰",
          description: `You've received ${tier.credits} AI chat credits (${tier.minutes} minutes)`,
        });

        // Invalidate user data to refresh credit balance
        queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
        setShowPaymentModal(false);
        onPaymentSuccess();

        // Reset states
        setIsProcessing(false);
        setSelectedTier(null);
        setPaymentStep('processing');
      }, 1500);

    } catch (error) {
      setPaymentStep('error');
      setTimeout(() => {
        toast({
          title: "Payment Failed",
          description: "Unable to process payment. Please try again.",
          variant: "destructive"
        });
        setShowPaymentModal(false);
        setIsProcessing(false);
        setSelectedTier(null);
        setPaymentStep('processing');
      }, 2000);
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-foreground mb-2">Choose Your AI Chat Plan</h2>
        <p className="text-muted-foreground">
          Get instant access to our AI mystical assistant for personalized guidance
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {paymentTiers.map((tier) => (
          <Card 
            key={tier.id} 
            className={`relative transition-all hover:scale-105 ${
              tier.popular 
                ? 'border-accent shadow-lg ring-2 ring-accent ring-opacity-20' 
                : 'border-border'
            }`}
          >
            {tier.popular && (
              <Badge className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-accent text-accent-foreground">
                Most Popular
              </Badge>
            )}

            <CardHeader className="text-center">
              <CardTitle className="text-xl">{tier.name}</CardTitle>
              <CardDescription>{tier.description}</CardDescription>
              <div className="mt-4">
                <span className="text-3xl font-bold text-foreground">${tier.price}</span>
                <span className="text-muted-foreground"> USD</span>
              </div>
            </CardHeader>

            <CardContent className="space-y-4">
              <div className="flex items-center justify-center space-x-4 text-sm text-muted-foreground">
                <div className="flex items-center">
                  <MessageCircle className="h-4 w-4 mr-1" />
                  {tier.credits} credits
                </div>
                <div className="flex items-center">
                  <Clock className="h-4 w-4 mr-1" />
                  {tier.minutes} minutes
                </div>
              </div>

              <ul className="space-y-2">
                {tier.features.map((feature, index) => (
                  <li key={index} className="flex items-center text-sm">
                    <Check className="h-4 w-4 text-accent mr-2 flex-shrink-0" />
                    {feature}
                  </li>
                ))}
              </ul>

              <Button
                onClick={() => handlePurchase(tier)}
                disabled={isProcessing}
                className={`w-full ${
                  tier.popular 
                    ? 'bg-accent hover:bg-accent/90' 
                    : 'bg-secondary hover:bg-secondary/90'
                }`}
                data-testid={`button-purchase-${tier.id}`}
              >
                {isProcessing && selectedTier?.id === tier.id ? (
                  <>
                    <Zap className="mr-2 h-4 w-4 animate-pulse" />
                    Processing...
                  </>
                ) : (
                  <>
                    <Zap className="mr-2 h-4 w-4" />
                    Get {tier.name} Plan
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="text-center text-sm text-muted-foreground">
        <p>ðŸ’³ Secure mock payment system (Demo mode)</p>
        <p>âœ¨ Credits are valid for 30 days from purchase</p>
      </div>

      {/* Mock Payment Modal */}
      <Dialog open={showPaymentModal} onOpenChange={() => {}}>
        <DialogContent className="sm:max-w-md"  onPointerDownOutside={(e) => e.preventDefault()}>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <CreditCard className="h-5 w-5" />
              {paymentStep === 'processing' && "Processing Payment"}
              {paymentStep === 'success' && "Payment Successful!"}
              {paymentStep === 'error' && "Payment Failed"}
            </DialogTitle>
            <DialogDescription>
              {paymentStep === 'processing' && `Processing your ${selectedTier?.name} plan purchase...`}
              {paymentStep === 'success' && `Your ${selectedTier?.name} plan has been activated!`}
              {paymentStep === 'error' && "There was an issue processing your payment."}
            </DialogDescription>
          </DialogHeader>

          <div className="flex flex-col items-center py-6">
            {paymentStep === 'processing' && (
              <div className="flex flex-col items-center space-y-4">
                <Loader2 className="h-12 w-12 animate-spin text-accent" />
                <div className="text-center">
                  <p className="text-sm text-muted-foreground mb-2">Please wait...</p>
                  <div className="flex items-center justify-center space-x-1">
                    <div className="w-2 h-2 bg-accent rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                    <div className="w-2 h-2 bg-accent rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                  </div>
                </div>
              </div>
            )}

            {paymentStep === 'success' && (
              <div className="flex flex-col items-center space-y-4">
                <div className="w-16 h-16 bg-green-100 dark:bg-green-900/30 rounded-full flex items-center justify-center">
                  <CheckCircle className="h-8 w-8 text-green-600 dark:text-green-400" />
                </div>
                <div className="text-center">
                  <p className="text-lg font-medium text-foreground">Payment Complete!</p>
                  <p className="text-sm text-muted-foreground">
                    {selectedTier?.credits} credits added to your account
                  </p>
                </div>
              </div>
            )}

            {paymentStep === 'error' && (
              <div className="flex flex-col items-center space-y-4">
                <div className="w-16 h-16 bg-red-100 dark:bg-red-900/30 rounded-full flex items-center justify-center">
                  <Zap className="h-8 w-8 text-red-600 dark:text-red-400" />
                </div>
                <div className="text-center">
                  <p className="text-lg font-medium text-foreground">Payment Failed</p>
                  <p className="text-sm text-muted-foreground">
                    Please try again or contact support
                  </p>
                </div>
              </div>
            )}
          </div>

          {selectedTier && (
            <div className="border-t pt-4">
              <div className="flex justify-between text-sm">
                <span className="text-muted-foreground">Plan:</span>
                <span className="font-medium">{selectedTier.name}</span>
              </div>
              <div className="flex justify-between text-sm">
                <span className="text-muted-foreground">Credits:</span>
                <span className="font-medium">{selectedTier.credits}</span>
              </div>
              <div className="flex justify-between text-sm">
                <span className="text-muted-foreground">Amount:</span>
                <span className="font-medium">${selectedTier.price} USD</span>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

